import java.util.Scanner;public class BinarySearch {    public static void main(String[] args) {        Scanner sc = new Scanner(System.in);        System.out.print("Enter the size of array: ");        int size = sc.nextInt();        System.out.print("Enter "+ size + " elements in array: ");        int[] array = new int[size];        for (int i = 0; i < array.length; i++) {            array[i] = sc.nextInt();        }        System.out.print("Enter the target element: ");        int target = sc.nextInt();        int ans = binarySearch(array, target);        System.out.print("Element found at index: "+ ans);    }    static int binarySearch(int[] arr, int target) {        int start = 0;        int end = arr.length - 1;        while(start <= end) {            int mid = start + (end - start) / 2;            if(target < arr[mid]) {                end = mid - 1;            } else if (target > arr[mid]){                start = mid + 1;            } else {                return mid;            }        }        return -1;    }}